import React, {Component} from 'react';
import EditExpense from '../edit-expense/edit-expense';



class ExpenseCondensed extends Component {
    
     constructor(props){
        super(props);  
        this.print=this.print.bind(this);        
    }

    print=()=>{
        console.log(this.props.expense);
    }

    
    
    render(){
        return(
            <li className="list-group-item expense-condensed">                        
                <p className="text-dark">{this.props.expense.title} | ${this.props.expense.cost}</p>
                <p className="text-dark">{this.props.expense.people}</p>
                <button className="btn btn-outline-danger text-dark" data-toggle="modal" data-target={"#meow"} onClick={() => this.print()}>Edit</button>
                <div className="modal fade" id={"meow"} tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog" role="document">
                    <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title text-dark" id="exampleModalLabel">Edit {this.props.expense.title}</h5>
                        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <div className="modal-body text-dark">
                        <form className="form-inline" onSubmit={this.handleSubmit}>
                            <div className="form-group mx-sm-3 mb-2">
                                <input type="text" className="form-control" id="title" placeholder="Enter updated title"></input>
                                <input type="text" className="form-control" id="cost" placeholder="Enter updated cost"></input>
                                <input type="text" className="form-control" id="peopleName" placeholder="Enter updated people"></input>
                            </div>
                        </form> 
                    </div>
                    <div class="modal-footer">
                        <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                        <button type="button" className="btn btn-primary">Save changes</button>
                    </div>
                    </div>
                </div>
                </div>
            </li>     
        )
    }
}

export default ExpenseCondensed;
