import React, {Component} from 'react';
import './edit-expense.css'



class EditExpense extends Component {
    
     constructor(props){
        super(props);
        this.doFunction = this.doFunction.bind(this);
                    
    }
   
    doFunction = () => {
        //        var XMLHttpRequest = require("xmlhttprequest").XMLHttpRequest;
                var xhr = new XMLHttpRequest();
                xhr.open("PUT", "http://192.168.68.114:3004/expense");
                xhr.setRequestHeader("Accept", "application/json");
                xhr.setRequestHeader("Content-Type", "application/json");
                xhr.onreadystatechange = function() {
                    if (xhr.readyState === 4) {
                        console.log(xhr.status);
                        console.log(xhr.responseText);
                    }
                }
                
                var title = document.getElementById("title").value;
                var cost = document.getElementById("cost").value;
                var people = document.getElementById("peopleName").value;
                // var completed = document.getElementById("completed").value;

                var obj = {};
                obj['eventId'] = this.props.expense.eventId;
                obj['expenseId'] = this.props.expense._id
                obj['title'] = title;
                obj['cost'] = cost;
                obj['people'] = people.split(',');
                // obj['completed'] = completed;
                
                var data = JSON.stringify(obj);
                alert(data)
                xhr.send(data);
                }
    
    render(){
        console.log(this.props.expense)
        return(            
            <div className="modal fade" id={JSON.stringify(this.props.expense._id)} tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog" role="document">
                <div className="modal-content">
                <div className="modal-header">
                    <h5 className="modal-title text-dark" id="exampleModalLabel">Edit {this.props.expense.title}</h5>
                    <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div className="modal-body text-dark">
                    <form className="form-inline" onSubmit={this.handleSubmit}>
                        <div className="form-group mx-sm-3 mb-2">
                            <input type="text" className="form-control" id="title" placeholder="Enter updated title"></input>
                            <input type="text" className="form-control" id="cost" placeholder="Enter updated cost"></input>
                            <input type="text" className="form-control" id="peopleName" placeholder="Enter updated people"></input>
                            <button type="button" className="btn btn-primary" onClick={() => this.doFunction()}>Save changes</button>
                        </div>
                    </form> 
                </div>
                <div class="modal-footer">
                    <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                    
                </div>
                </div>
            </div>
            </div>
        )
    }
}

export default EditExpense;
