import React, {Component} from 'react';
import EditExpense from '../edit-expense/edit-expense';



class ExpenseCondensed extends Component {
    
     constructor(props){
        super(props);  
        this.print=this.print.bind(this);    
        this.deleteExpense=this.deleteExpense.bind(this);    
        this.createPeopleList=this.createPeopleList.bind(this);
    }

    print=()=>{
        console.log(this.props.expense);
    }

    deleteExpense = () => {
        console.log(this.props.expense)
        var xhr = new XMLHttpRequest();
        xhr.open("DELETE", "http://192.168.68.114:3004/expense/remove");
        xhr.setRequestHeader("Accept", "application/json");
        xhr.setRequestHeader("Content-Type", "application/json");
        xhr.onreadystatechange = function() {
            if (xhr.readyState === 4) {
                console.log(xhr.status);
                console.log(xhr.responseText);
            }
        }
        var obj ={};
        obj['expenseId'] = this.props.expense._id;    
        var data = JSON.stringify(obj);
        alert(data)
        xhr.send(data);
        
        }
    createPeopleList = () =>{
        const list = this.props.expense.people((people) =>
            <button type="button" class="btn btn-outline-warning">{people}</button>
        );
        return (list);
    }
    render(){     
        return(
            <div>                
                <li className="list-group-item expense-condensed">                        
                    <p className="text-dark">{this.props.expense.title} | ${this.props.expense.cost}</p>
                    <h6 className="text-dark">People involved: {this.createPeopleList()}</h6>
                    <h6 className="text-dark">Paid by: {this.props.expense.paidBy}</h6>
                    <button className="btn btn-outline-primary text-dark" data-toggle="modal" data-target={"#"+JSON.stringify(this.props.expense._id)} onClick={() => this.print()}>Edit</button>                
                    <button className="btn btn-outline-danger text-dark" onClick={() => this.deleteExpense()}>Remove</button>   
                </li> 

                <EditExpense expense={this.props.expense}/>
            </div>
            
        )
    }
}

export default ExpenseCondensed;
