import React, {Component} from 'react';
import EditExpense from '../edit-expense/edit-expense';



class ExpenseCondensed extends Component {
    
     constructor(props){
        super(props);  
        this.print=this.print.bind(this);        
    }

    print=()=>{
        console.log(this.props.expense);
    }

    
    
    render(){
        var paidBy = this.props.expense.paidBy;
        return(
            <div>                
                <li className="list-group-item expense-condensed">                        
                    <p className="text-dark">{this.props.expense.title} | ${this.props.expense.cost}</p>
                    <p className="text-dark">People involved: {this.props.expense.people}</p>
                    <p className="text-dark">Paid by: {paidBy}</p>
                    <div class="form-check">
                        <input class="form-check-input" type="checkbox" value="" id="defaultCheck1"></input>
                        <label class="form-check-label text-dark" for="defaultCheck1">
                            Completed
                        </label>
                        if (){

                        }
                    </div>   
                    <button className="btn btn-outline-danger text-dark" data-toggle="modal" data-target={"#"+JSON.stringify(this.props.expense._id)} onClick={() => this.print()}>Edit</button>                
                </li> 

                <EditExpense expense={this.props.expense}/>
            </div>
            
        )
    }
}

export default ExpenseCondensed;
