import React, {Component} from 'react';
import './event-condensed.css';
import EditEvent from '../edit-event/edit-event';

class EventCondensed extends Component {
    
     constructor(props){
        super(props);
        this.createEventPeople= this.createEventPeople.bind(this);
        // this.createEventPeopleList= this.createEventPeopleList.bind(this);
        this.deleteMember = this.deleteMember.bind(this);
                    
    }

    createEventPeople = () =>{
        const list = this.props.eventData.event_top.people?.map((people) =>
        <button key={people} type="button" className="btn btn-warning btn-eventPeople">{people} <button id={people} className="btn btn-warning" onClick={this.deleteMember}>x</button></button>
        );
        return (list);
    }

    deleteMember = (event) => {
        const id = event.target.id;
        console.log("deleting"+id)
        var xhr = new XMLHttpRequest();
        xhr.open("DELETE", "http://localhost:3004/event/member");
        xhr.setRequestHeader("Accept", "application/json");
        xhr.setRequestHeader("Content-Type", "application/json");
        xhr.onreadystatechange = function() {
            if (xhr.readyState === 4) {
                console.log(xhr.status); 
                console.log(xhr.responseText);
            }
        }

        var obj ={};
        obj['eventId'] = this.props.eventData.event_top.uniqueId;  
        obj['removeMember'] = id;   
        var data = JSON.stringify(obj);
        alert(data)
        xhr.send(data);

        xhr.onload = () => {
            if (xhr.readyState === xhr.DONE) {
              if (xhr.status === 200) {
                this.props.eventData.loadEventCondensed(this.props.eventData.IdReload_event);
                this.props.eventData.loadExpenseCondensed(this.props.eventData.IdReload_event);
              }
            }
          };
        }

    
    render(){
        console.log("sartat")
        return(
            <div>
                <li>                        
                    <h1 className="text-light event-title">{this.props.eventData.event_top.title} <button className="btn btn-primary text-light btn-sm btn-editEvent" data-toggle="modal" data-target={"#"+JSON.stringify(this.props.eventData.event_top._id)}>Edit Event Title</button> </h1>
                    <div className="text-dark event-people">{this.createEventPeople()}<button className="btn btn-success btn-eventPeople btn-addPeople" data-toggle="modal" data-target="#addMember">+</button></div>
                    <div className="modal" id={JSON.stringify(this.props.eventData.event._id)} tabindex="-1" role="dialog" aria-labelledby="editEventLModal" aria-hidden="true">
                        <div className="modal-dialog" role="document">
                            <div className="modal-content">
                            <div className="modal-header">
                                {/* <h5 className="modal-title text-dark" id="editEventLModal">Edit {this.props.eventData.event.title}</h5> */}
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                                </button>
                            </div>
                            <div className="modal-body text-dark">
                                <form className="form" onSubmit={this.handleSubmit}>
                                    <div className="form-group mx-sm-3 mb-2">
                                        <input type="text" className="form-control" id={"editEventTitle"+JSON.stringify(this.props.eventData.event._id)} placeholder={this.props.eventData.event.title }></input>                      
                                    </div>
                                </form> 
                            </div>
                            <div class="modal-footer">
                                <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                                <button type="button" className="btn btn-primary" onClick={() => this.doFunction()} data-dismiss="modal">Save changes</button>
                                
                            </div>
                            </div>
                        </div>
                    </div>
                </li>

                <EditEvent eventData={{event:this.props.eventData.event_top, loadEventCondensed: this.props.eventData.loadEventCondensed, IdReload_event:this.props.eventData.IdReload_event}}/>
            </div>
            
        )
    }
}

export default EventCondensed;
