import React, {Component} from 'react';
import ExpenseCondensed from '../expense-condensed/expense-condensed';
import './expense.css';
import HttpService from '../services/http-services';

const http = new HttpService();
class Expense extends Component {

    constructor(props){
        super(props);   
        this.state = {expenses:[]};
        this.state = {events:[]};
        this.loadData = this.loadData.bind(this);
        this.Id = this.props.data.id
        this.loadData(this.Id);
        this.doFunction = this.doFunction.bind(this);
        this.printexpenses = this.printexpenses.bind(this);
        this.createExpenseList = this.createExpenseList.bind(this);
        this.displayEventInfo = this.displayEventInfo.bind(this);
    }

    loadData = (id) => {
        console.log(this.props.data)
        if (id.length!=0){
        http.getExpenses(id).then(data => {
           this.setState({expenses:data});
           console.log(this.state.expenses);
        }, err => {});
        }
        else{
            console.log("no Id")
        }
    }

    printexpenses = ()=>{
        console.log(this.state.expenses)
    }

    displayEventInfo = () =>{
        const list = this.state.events.map((events) =>
            <EventCondensed events={events} />
        );
        return (list);
    }

    createExpenseList = () =>{
        const list = this.state.expenses.map((expense) =>
            <ExpenseCondensed expense={expense} />
        );
        return (list);
    }

    doFunction = () => {
        //        var XMLHttpRequest = require("xmlhttprequest").XMLHttpRequest;
                var xhr = new XMLHttpRequest();
                xhr.open("POST", "http://localhost:3004/expense");
                xhr.setRequestHeader("Accept", "application/json");
                xhr.setRequestHeader("Content-Type", "application/json");
                xhr.onreadystatechange = function() {
                    if (xhr.readyState === 4) {
                        console.log(xhr.status);
                        console.log(xhr.responseText);
                    }
                }
                
                var title = document.getElementById("title").value;
                var cost = document.getElementById("cost").value;
                var people = document.getElementById("peopleName").value;
                var completed = document.getElementById("completed").value;
                var obj = {};
                obj['title'] = title;
                obj['cost'] = cost;
                obj['people'] = people.split(',');
                obj['completed'] = completed;
                var data = JSON.stringify(obj);
        
                xhr.send(data);
        //
                }
    
              
        
    render(){
        
        return(
            <div className="expense">
                <header className="App-header">
                    <button onClick ={()=>this.printexpenses()}>print expenses</button> 
                    <div className="container-fluid App-main">      
                        <h1 className="event-title">{this.props.data.eventTitle}</h1>  
                        <p className="event-people"> {this.props.data.eventPeople}</p>
                        <div className="row">
                            <div className='col-sm-12 add-expense'>
                                <form className="form-inline">
                                    <div className="form-group mx-sm-3 mb-2">
                                        <input type="text" className="form-control" id="title" placeholder="Title"></input>
                                        <input type="text" className="form-control" id="cost" placeholder="Cost"></input>
                                        <input type="text" className="form-control" id="peopleName" placeholder="People"></input>
                                        <input type="text" className="form-control" id="completed" placeholder="Completed"></input>
                                    </div>
                                    <a href="#"><button type="submit" className="btn btn-primary mb-2" onClick={() => this.doFunction()}>Add New Expense</button></a>
                                </form> 
                            </div>
                        </div>              
                        <ul className="list-group-item expense-condensed">  
                            {this.createExpenseList()}
                        </ul>
                    </div>
                </header>
            </div>
            );
    }
}

    

export default Expense;
