import React, {Component} from 'react';
import EditExpense from '../edit-expense/edit-expense';
import './expense-condensed.css'


class ExpenseCondensed extends Component {
    
     constructor(props){
        super(props);  
        this.print=this.print.bind(this);    
        this.deleteExpense=this.deleteExpense.bind(this);    
        this.createPeopleList=this.createPeopleList.bind(this);
    }

    print=()=>{
        console.log(this.props.expenseData.expense);
    }

    deleteExpense = () => {
        console.log(this.props.expenseData.expense)
        var xhr = new XMLHttpRequest();
        xhr.open("DELETE", "http://localhost:3004/expense/remove");
        xhr.setRequestHeader("Accept", "application/json");
        xhr.setRequestHeader("Content-Type", "application/json");
        xhr.onreadystatechange = function() {
            if (xhr.readyState === 4) {
                console.log(xhr.status);
                console.log(xhr.responseText);
            }
        }
        var obj ={};
        obj['expenseId'] = this.props.expenseData.expense._id;    
        var data = JSON.stringify(obj);
        alert(data)
        xhr.send(data);

        xhr.onload = () => {
            if (xhr.readyState === xhr.DONE) {
              if (xhr.status === 200) {
                this.props.expenseData.loadExpenseCondensed(this.props.expenseData.IdReload_expense);
                
              }
            }
          };
        }

    createPeopleList = (inputlist, color) =>{
        const list = inputlist.map((people) =>
            <button type="button" className={"btn btn-"+color+" btn-people"}>{people}</button>
    );
        return (list);
    }
    render(){     
        return(
            <div className='d-flex flex-column'>                
                <li className="list-group-item expense-condensed ">                        
                    <p className="text-dark">{this.props.expenseData.expense.title} | ${this.props.expenseData.expense.cost}</p>
                    <h6 className="text-dark">People involved: {this.createPeopleList(this.props.expenseData.expense.people,'warning')}</h6>
                    <h6 className="text-dark">Paid by: {this.createPeopleList([this.props.expenseData.expense.paidBy],'success')}</h6>
                    <button className="btn btn-outline-primary text-dark btn-sm" data-toggle="modal" data-target={"#"+JSON.stringify(this.props.expenseData.expense._id)} onClick={() => this.print()}>Edit</button>                
                    <button className="btn btn-outline-danger text-dark btn-sm" onClick={() => this.deleteExpense()}>Remove</button>   
                </li> 

                <EditExpense expenseData={{expense:this.props.expenseData.expense,loadExpenseCondensed: this.props.expenseData.loadExpenseCondensed, IdReload_expense:this.props.expenseData.IdReload_expense}}/>
                {/* expenseData={{expense:this.props.expenseData.expense,loadExpenseCondensed: this.props.expenseData.loadExpenseCondensed, eventIdReload:this.props.expenseData.eventIdReload}} */}
            </div>
            
        )
    }
}

export default ExpenseCondensed;
