import 'whatwg-fetch';

class HttpService {
    getExpenses = (id) => {
        var promise = new Promise((resolve, reject) => {
            fetch('http://localhost:3004/expense/all', {
            method: 'POST',
            body: JSON.stringify({'eventId': id})
            }).then(response => {
                resolve(response.json());
            })
        });
        return promise;
    }
}

export default HttpService;
