import React, {Component} from 'react';
import './event-condensed.css';


class EventCondensed extends Component {
    
     constructor(props){
        super(props);
        this.createEventPeople= this.createEventPeople.bind(this);
                    
    }

    createEventPeople = () =>{
        const list = this.props.event_top.people.map((people) =>
        <button type="button" className="btn btn-warning btn-eventPeople">{people+" x"}</button>
        );
        return (list);
    }

    
    render(){
        console.log("sartat")
        return(
            <li>                        
                <h1 className="text-light event-title">{this.props.event_top.title}</h1>
                <p className="text-light event-people">{this.props.event_top.people}</p>
                <button className="btn btn-outline-primary text-light btn-sm" data-toggle="modal" data-target={"#"+JSON.stringify(this.props.event_top._id)}>Edit Event</button> 
            </li>     
        )
    }
}

export default EventCondensed;
